int minDistinctSplit(char *s, int len){
   int *suffix=(int *)malloc(sizeof(int) * len), *prefix=(int *)malloc(sizeof(int) * len);
    memset(suffix, 0, len*sizeof(int));
    memset(prefix, 0, len*sizeof(int));
    
    //left to right distinct 
    int distinct=0, hash[26]={0};
    memset(hash, 0, 26);
    
    for(int i=0; i<len; i++){
        hash[s[i]-97]++;
        if(hash[s[i]-97]==1)
            distinct++;
        prefix[i] = distinct;
    }//for
    
    //right to left
    distinct=0;
    memset(hash, 0, 26*sizeof(int));

    for(int i=len-1; i>=0; i--){
        hash[s[i]-97]++;
        if(hash[s[i]-97]==1)
            distinct++;
        suffix[i] = distinct;
    }//for 
    
    // for(int i=0;i<len;i++)
    //     printf("%d %d\n", prefix[i], suffix[i]);
    
    int left=0, right=1;
    distinct=0;
    while(left < len-1 && right > 0){
        // printf("left %d right %d", prefix[left], suffix[right]);
        if(prefix[left] == suffix[right])
            distinct++;
        left++;
        right++;        
    }//while
    return distinct;
}//

int numSplits(char * s){
    int len = strlen(s);
    if(len==1)
        return 0;
    
    if(len==2)
        return 1;
    
    return minDistinctSplit(s, len);
}

int main(){
  char *s="aaaaa";
  printf("%d\n", numSplits(s));
  return 0;
}

